bullet.text:
var colors = ['red', 'orange', 'yellow', 'green', 'blue', 'indigo', 'violet'];


console.log(colors);
// ['red', 'orange', 'yellow', 'green', 'blue', 'indigo', 'violet']


console.log('Reversing the colors array.');


colors.reverse();


console.log(colors);
// ['violet', 'indigo', 'blue', 'green', 'yellow', 'orange', 'red']
