var colors = ['red', 'orange', 'yellow', 'green', 'blue', 'indigo', 'violet'];


console.log(colors);
// ['red', 'orange', 'yellow', 'green', 'blue', 'indigo', 'violet']


console.log('Sorting the colors array.');


colors.sort();


console.log(colors);
// ['blue', 'green', 'indigo', 'orange', 'red', 'violet', 'yellow']
